mapper.update:
UPDATE CPR_CARD
SET CPR_CARD_NM = #{cprCardNm}
WHERE CPR_CARD_NO = #{cprCardNo}

UPDATE CPR_CARD
SET CPR_CARD_STTUS = 2
WHERE CPR_CARD_NO = #{cprCardNo}

{CALL
    DECLARE
    BEGIN
        UPDATE CPR_CARD_RESVE
        SET CPR_CARD_RESVE_RTURN_AT = 1
        WHERE CPR_CARD_RESVE_SN = #{cprCardResveSn};

        UPDATE CPR_CARD
        SET CPR_CARD_STTUS = 0
        WHERE CPR_CARD_NO = #{cprCardNo};
    END}
