mapper.insert:
insert into EMAIL_FROM( email_etpr_code
                      , email_from_addr
                      , EMAIL_SN
                      , email_from_sj
                      , email_from_cn
                      , email_from_cn_type
                      , email_from_send_date
                      , EMAIL_FROM_TMPR_STRE_AT)
values ( 'EMAIL-' || EMAIL_SEQ.nextval || '-' || to_char(#{emailFromSendDate}, 'yyyymmdd')
       , #{emailFromAddr}
       , #{emailSn}
       , #{emailFromSj}
       , #{emailFromCn}
       , #{emailFromCnType}
       , #{emailFromSendDate}
       , #{emailFromTmprStreAt})

insert into EMAIL_TO( email_etpr_code
                    , email_from_addr
                    , EMAIL_SN
                    , email_to_addr
                    , email_to_received_date)
values ( 'EMAIL-' || EMAIL_SEQ.currval || '-' || to_char(#{emailFromSendDate}, 'yyyymmdd')
       , #{emailFromAddr}
       , #{emailSn}
       , #{emailToAddr}
       , #{emailToReceivedDate})

insert into EMAIL_CC( email_etpr_code
                    , email_from_addr
                    , email_sn
                    , email_cc_addr
                    , EMAIL_CC_RECEIVED_DATE)
values ( 'EMAIL-' || EMAIL_SEQ.currval || '-' || to_char(#{emailFromSendDate}, 'yyyymmdd')
       , #{emailFromAddr}
       , #{emailSn}
       , #{emailCcAddr}
       , #{emailCcReceivedDate})

insert into EMAIL_RECEIVED_STTUS( email_etpr_code
                                , email_from_addr
                                , email_received_empl_id
                                , email_sn
                                , email_redng_at
                                , email_delete_at
                                , email_imprtnc_at)
VALUES ( 'EMAIL-' || EMAIL_SEQ.currval || '-' || to_char(#{emailFromSendDate}, 'yyyymmdd')
       , #{emailFromAddr}
       , #{emailReceivedEmplId}
       , #{emailSn}
       , #{emailRedngAt}
       , #{emailDeleteAt}
       , #{emailImprtncAt})

mapper.select:
select count(EMPL_ID)
from empl
where EMPL_EMAIL = #{emailAddr}
