mapper.update:
UPDATE EMPL
SET EMPL_MACADRS = #{emplMacadrs}
WHERE EMPL_ID = #{emplId}

UPDATE NTCN_AT
SET COMMON_CODE_NTCN_AT =
        CASE
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN010' THEN #{notificationVO.dutyRequest}
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN011' THEN #{notificationVO.answer}
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN012' THEN #{notificationVO.teamNotice}
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN013' THEN #{notificationVO.companyNotice}
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN014' THEN #{notificationVO.schedule}
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN015' THEN #{notificationVO.newChattingRoom}
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN016' THEN #{notificationVO.emailReception}
            WHEN COMMON_CODE_NTCN_KIND = 'NTCN017' THEN #{notificationVO.electronSanctionReception}
            ELSE COMMON_CODE_NTCN_AT
            END
WHERE COMMON_CODE_NTCN_KIND IN (
                                'NTCN010',
                                'NTCN011',
                                'NTCN012',
                                'NTCN013',
                                'NTCN014',
                                'NTCN015',
                                'NTCN016',
                                'NTCN017',
                                'NTCN018'
    )
  AND NTCN_AT_EMPL_ID = #{emplId}

{call declare
begin
    UPDATE EMPL
    SET EMPL_PASSWORD = #{emplPassword}
    WHERE EMPL_ID = #{emplId};

    UPDATE AUTH
    SET AUTH_CODE = 'ROLE_EMP'
    WHERE AUTH_EMP_ID = #{emplId};
end}

mapper.insert:
{call declare begin -- 회원 정보 입력 INSERT INTO EMPL (EMPL_ID, EMPL_PASSWORD, EMPL_NM, EMPL_TELNO, EMPL_EMAIL, EMPL_ZIP, EMPL_ADRES, EMPL_DETAIL_ADRES, EMPL_ENCPN, EMPL_BRTHDY, ENABLED, COMMON_CODE_DEPT, COMMON_CODE_CLSF, COMMON_CODE_HFFC_STTUS, COMMON_CODE_LAST_ACDMCR) VALUES (#{emplId}, #{emplPassword}, #{emplNm}, #{emplTelno}, #{emplEmail}, #{emplZip}, #{emplAdres}, #{emplDetailAdres}, #{emplEncpn}, #{emplBrthdy}, #{enabled}, #{commonCodeDept}, #{commonCodeClsf}, #{commonCodeHffcSttus}, #{commonCodeLastAcdmcr}); INSERT INTO AUTH(AUTH_CODE, AUTH_EMP_ID) VALUES ('ROLE_NEW', #{emplId});
INSERT INTO PROFL(PROFL_ETPR_CODE, PROFL_EMPL_ID) VALUES ('PROFL-' || PROFL_SEQ.nextval || '-' || #{emplId}, #{emplId});
INSERT INTO SIGN(SIGN_ETPR_CODE, SIGN_EMPL_ID) VALUES ('SIGN-' || SIGN_SEQ.nextval || '-' || #{emplId}, #{emplId});
INSERT INTO UPLOAD_FILE(UPLOAD_FILE_SN, UPLOAD_FILE_ETPR_CODE, UPLOAD_FILE_ORGINL_NM, UPLOAD_FILE_STRE_NM, UPLOAD_FILE_SIZE, UPLOAD_FILE_RGSDE) VALUES (UPLOAD_FILE_SEQ.nextval, (SELECT PROFL_ETPR_CODE FROM PROFL WHERE PROFL_EMPL_ID = #{emplId}), 'default', #{proflPhotoFileStreNm}, 0, sysdate);
INSERT INTO UPLOAD_FILE(UPLOAD_FILE_SN, UPLOAD_FILE_ETPR_CODE, UPLOAD_FILE_ORGINL_NM, UPLOAD_FILE_STRE_NM, UPLOAD_FILE_SIZE, UPLOAD_FILE_RGSDE) VALUES (UPLOAD_FILE_SEQ.nextval, (SELECT SIGN_ETPR_CODE FROM SIGN WHERE SIGN_EMPL_ID = #{emplId}), 'default', #{signPhotoFileStreNm}, 0, sysdate);
INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN010', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN011', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN012', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN013', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN014', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN015', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN016', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN017', #{emplId}, 'NTCN_AT010'); INSERT INTO NTCN_AT (COMMON_CODE_NTCN_KIND, NTCN_AT_EMPL_ID, COMMON_CODE_NTCN_AT) VALUES ('NTCN018', #{emplId}, 'NTCN_AT010'); end}

mapper.update:
UPDATE EMPL
SET EMPL_PASSWORD = #{emplPassword}
WHERE EMPL_ID = #{emplId}
