mapper.select:
/* 결재함 기안, 완료, 반려 결재 개수 불러오기 */
SELECT
    COUNT(*)
FROM
    ELCTRN_SANCTN
WHERE
    ELCTRN_SANCTN_DRFT_EMPL_ID = #{elctrnSanctnDrftEmplId}
  AND
    COMMON_CODE_SANCT_PROGRS = #{commonCodeSanctProgrs}

mapper.update:
/* 결재 승인 - 다음 결재 순번자의 상태 변경 */ {CALL DECLARE BEGIN UPDATE SANCTN_LINE SET COMMON_CODE_SANCT_PROGRS = 'SANCTN016', SANCTN_LINE_DATE = SYSDATE WHERE ELCTRN_SANCTNEMPL_ID = #{elctrnSanctnemplId} AND ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode};
UPDATE ELCTRN_SANCTN SET COMMON_CODE_SANCT_PROGRS = 'SANCTN011' WHERE ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode};
UPDATE SANCTN_LINE SET COMMON_CODE_SANCT_PROGRS = 'SANCTN013' WHERE SANCTN_LINE_ORDR = (SELECT SANCTN_LINE_ORDR FROM SANCTN_LINE WHERE ELCTRN_SANCTNEMPL_ID = #{elctrnSanctnemplId} AND ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode}) + 1 AND ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode}; END}

/* 결재 최종 승인 - 결재 문서의 상태 변경 */
{CALL DECLARE
BEGIN
    UPDATE SANCTN_LINE
    SET COMMON_CODE_SANCT_PROGRS = 'SANCTN016',
        SANCTN_LINE_DATE         = SYSDATE
    WHERE ELCTRN_SANCTNEMPL_ID = #{elctrnSanctnemplId}
      AND ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode};

    UPDATE ELCTRN_SANCTN
    SET COMMON_CODE_SANCT_PROGRS = 'SANCTN016',
        ELCTRN_SANCTN_FINAL_DATE = SYSDATE
    WHERE ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode};
END}

/* 결재 반려 - 결재 문서의 상태 변경 및 반려 사유 등록 */
{CALL DECLARE
BEGIN
    UPDATE SANCTN_LINE
    SET COMMON_CODE_SANCT_PROGRS = 'SANCTN015',
        SANCTN_LINE_DATE         = SYSDATE,
        SANCTN_LINE_RETURN_RESN  = #{sanctnLineReturnResn}
    WHERE ELCTRN_SANCTNEMPL_ID = #{elctrnSanctnemplId}
      AND ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode};

    UPDATE ELCTRN_SANCTN
    SET COMMON_CODE_SANCT_PROGRS = 'SANCTN015'
    WHERE ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode};
END}

/* 결재 회수 - 결재 문서의 상태 변경 */
UPDATE ELCTRN_SANCTN
SET COMMON_CODE_SANCT_PROGRS = 'SANCTN012'
WHERE ELCTRN_SANCTN_ETPR_CODE = #{elctrnSanctnEtprCode}

mapper.delete:
/* 나의 결재선 삭제  */
DELETE
FROM SANCTN_LINE_BOOKMARK
WHERE SANCTION_LINE_BOOKMARK_SN = #{sanctionLineBookmarkSn}
